import "./App.css";
import "./bootstrap.css";
import "./style.css";
import React from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Login from './pages/login';
import Register from './pages/register';
import Home from "./home";
import NotFound from './pages/404';

// Buat sebuah fungsi middleware untuk memeriksa otentikasi pengguna
const requireAuth = (props) => {
  // Implementasi logika otentikasi di sini...
  const isAuthenticated = false; // Ganti dengan logika otentikasi sesungguhnya
  if (!isAuthenticated) {
    // Jika pengguna tidak terotentikasi, arahkan mereka ke halaman login
    return <Navigate to="/login" />;
  }
  // Jika pengguna terotentikasi, lanjutkan ke rute yang diminta
  return <>{props.children}</>;
};

class App extends React.Component {
  render() {
    return (
      <BrowserRouter>
        <div>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            {/* Tambahkan middleware requireAuth ke rute yang memerlukan otentikasi */}
            <Route path="/" element={<Home />}>
              <Route element={<requireAuth />}/>
            </Route>
            <Route path="/*" element={<NotFound />} />
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
