import logo from './logo.svg';
import './App.css';
import './bootstrap.css';
import './style.css';

function App() {
  return (
    <div className="App">
        <main class="mt-5">
            <div class="container py-5">
                <div class="row justify-content-center">
                    <div class="col-md-5">
                        <div class="card">
                            <div class="card-header d-flex align-items-center justify-content-between bg-transparent py-3">
                                <h5 class="mb-0">Login</h5>
                            </div>
                            <div class="card-body">
                                <form>
                                    <div class="mb-2">
                                        <label for="username">Username</label>
                                        <input type="text" class="form-control" id="username" name="username"/>
                                    </div>

                                    <div class="mb-3">
                                        <label for="password">Password</label>
                                        <input type="password" class="form-control" id="password" name="password"/>
                                    </div>

                                    <button type="submit" class="btn btn-primary w-100" onClick="login()">Login</button>

                                </form>
                            </div>
                        </div>

                        <div class="text-center mt-4">
                            Don't have account? <a href="register.html">Register</a>
                        </div>

                    </div>
                </div>
            </div>
        </main>
    </div>
  );
}

function login() {
  const username = document.getElementById('username').value;
  const password = document.getElementById('password').value;

  fetch('http://localhost:8000/api/v1/auth/login', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
  })
  .then(response => {
      if (response.ok) {
          window.location.href = 'homepage.html'; // Redirect ke halaman homepage.html jika berhasil
      } else {
          throw new Error('Login failed');
      }
  })
  .catch(error => {
      console.error('Login error:', error);
      // Handle login error, misalnya dengan menampilkan pesan error kepada pengguna
  });
}


export default App;
