import "./App.css";
import "./bootstrap.css";
import "./style.css";
import React, {U} from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Login from './pages/login';
import Register from './pages/register';
import Home from "./home";
import NotFound from './pages/404';
import axios from "axios";

const RequireAuth = (props) => {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      // Token tidak ada, arahkan ke halaman login
      return;
    }

    const authorizationHeader = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };

    axios.post('http://localhost:8000/api/v1/auth/token', null, authorizationHeader)
      .then(response => {
        // Token valid, set state menjadi terotentikasi
        setAuthenticated(true);
      })
      .catch(error => {
        // Token tidak valid, atau ada kesalahan lain
        console.error("Error:", error);
      });
  }, []); // Efek hanya dijalankan sekali setelah komponen dimuat

  if (!authenticated) {
    // Jika tidak terotentikasi, arahkan ke halaman login
    return <Navigate to="/login" />;
  }

  // Jika terotentikasi, render komponen anak
  return <>{props.children}</>;
};

class App extends React.Component {
  render() {
    return (
      <BrowserRouter>
        <div>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/" element={<RequireAuth><Home /></RequireAuth>}/>
            <Route path="/*" element={<NotFound />} />
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
