import './App.css';
import './bootstrap.css';
import './style.css';
import { useState } from 'react';
import axios from 'axios';

function App() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/v1/auth/login', {
                username,
                password
            });
            if (response.status === 200) {
                window.location.href = 'homepage.html'; // Redirect ke halaman homepage.html jika berhasil
            } else {
                throw new Error('Login failed');
            }
        } catch (error) {
            console.error('Login error:', error);
            // Handle login error, misalnya dengan menampilkan pesan error kepada pengguna
        }
    };

    return (
        <div className="App">
            <main className="mt-5">
                <div className="container py-5">
                    <div className="row justify-content-center">
                        <div className="col-md-5">
                            <div className="card">
                                <div className="card-header d-flex align-items-center justify-content-between bg-transparent py-3">
                                    <h5 className="mb-0">Login</h5>
                                </div>
                                <div className="card-body">
                                    <form>
                                        <div className="mb-2">
                                            <label htmlFor="username">Username</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="username"
                                                name="username"
                                                value={username}
                                                onChange={(e) => setUsername(e.target.value)}
                                            />
                                        </div>
                                        <div className="mb-3">
                                            <label htmlFor="password">Password</label>
                                            <input
                                                type="password"
                                                className="form-control"
                                                id="password"
                                                name="password"
                                                value={password}
                                                onChange={(e) => setPassword(e.target.value)}
                                            />
                                        </div>
                                        <button
                                            type="button"
                                            className="btn btn-primary w-100"
                                            onClick={handleLogin}
                                        >
                                            Login
                                        </button>
                                    </form>
                                </div>
                            </div>
                            <div className="text-center mt-4">
                                Don't have account? <a href="register.html">Register</a>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default App;
