import "./App.css";
import "./bootstrap.css";
import "./style.css";
import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Login from './pages/login';
import Register from './pages/register';
import Home from "./home";
import NotFound from './pages/404';
import axios from "axios";

const RequireAuth = (props) => {
  const [authStatus, setAuthStatus] = useState('pending');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setAuthStatus('unauthorized');
    } else {
      const authorizationHeader = axios.create({
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      authorizationHeader.post('http://localhost:8000/api/v1/auth/token')
        .then((response) => {
          setAuthStatus('authorized');
        })
        .catch((error) => {
          setAuthStatus('unauthorized');
        });
    }
  }, []);

  if (authStatus === 'pending') {
    // Tampilkan apa pun yang sesuai selama verifikasi berlangsung
    return <div><div id="loading"  className="loading e"></div>Mengauthentikasi...</div>;
  } else if (authStatus === 'unauthorized') {
    // Navigasi ke halaman login jika tidak ada token atau token tidak valid
    return <Navigate to="/login" />;
  } else {
    // Render komponen anak jika terotentikasi
    return props.children;
  }
};

const App = () => {
  return (
    <BrowserRouter>
      <div>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/" element={<RequireAuth><Home /></RequireAuth>} />
          <Route path="/*" element={<NotFound />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
