import "./App.css";
import "./bootstrap.css";
import "./style.css";
import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Navigate, useNavigate } from "react-router-dom";
import Login from './pages/login';
import Register from './pages/register';
import Home from "./home";
import NotFound from './pages/404';
import axios from "axios";

const RequireAuth = ({ children }) => {
  const [authStatus, setAuthStatus] = useState('pending');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setAuthStatus('unauthorized');
    } else {
      const authorizationHeader = axios.create({
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      authorizationHeader.post('http://localhost:8000/api/v1/auth/token')
        .then((response) => {
          setAuthStatus('authorized');
        })
        .catch((error) => {
          setAuthStatus('unauthorized');
        });
    }
  }, []);

  if (authStatus === 'pending') { 
    return <div className="d-flex justify-content-center align-items-center h-100 1-100 flex-column"><div id="loading"  className="loading"></div>Mengauthentikasi...</div>;
  } else if (authStatus === 'unauthorized') {
    // Jangan lakukan navigasi di sini
    return null;
  } else {
    return children;
  }
};

const LogoutAuth = () => {
  const navigate = useNavigate();

  localStorage.removeItem('token'); // Remove token from localStorage

  // Redirect to login page after logout
  navigate('/login');

  return null; // Return null to prevent rendering unnecessary content
};

const App = () => {
  return (
    <BrowserRouter>
      <div>
        <Routes>
          <Route path="/login" element={<RequireAuth><Login /></RequireAuth>} />
          <Route path="/register" element={<RequireAuth><Register /></RequireAuth>} />
          <Route path="/" element={<RequireAuth><Home /></RequireAuth>} />
          <Route path="/*" element={<NotFound />} />
          <Route path="/logout" element={<LogoutAuth />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
