import "./App.css";
import "./bootstrap.css";
import "./style.css";
import React, {useNavigate, useEffect} from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Login from './pages/login';
import Register from './pages/register';
import Home from "./home";
import NotFound from './pages/404';
import axios from "axios";

const RequireAuth = (props) => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login'); // Navigasi jika tidak ada token
    } else {
      const authorizationHeader = axios.create({
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      // Verifikasi token
      authorizationHeader.post('http://localhost:8000/api/v1/auth/token')
        .then((response) => {
          // Jika verifikasi berhasil, render komponen anak
        })
        .catch((error) => {
          navigate('/login'); // Navigasi jika verifikasi gagal
        });
    }
  }, [token, navigate]);

  // Render komponen anak
  return props.children;
};

class App extends React.Component {
  render() {
    return (
      <BrowserRouter>
        <div>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/" element={<RequireAuth><Home /></RequireAuth>}/>
            <Route path="/*" element={<NotFound />} />
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
