import "./App.css";
import "./bootstrap.css";
import React, { Component } from "react";
import { BrowserRouter, Route, Link, Routes } from "react-router-dom";
import axios from "axios";

function LoginPage({ doLogin }) {
  const handleSubmit = (e) => {
    e.preventDefault();
    const username = e.target.elements.username.value;
    const password = e.target.elements.password.value;
    doLogin(username, password);
  };

  return (
    <main className="mt-5">
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-md-5">
            <div className="card">
              <div className="card-header d-flex align-items-center justify-content-between bg-transparent py-3">
                <h5 className="mb-0">Login</h5>
              </div>
              <div className="card-body">
              <div className="alert alert-danger text-center d-none" id="alert"></div>
                <form onSubmit={handleSubmit}>
                  <div className="mb-2">
                    <label htmlFor="username">Username</label>
                    <input
                      type="text"
                      className="form-control"
                      id="username"
                      name="username"
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="password">Password</label>
                    <input
                      type="password"
                      className="form-control"
                      id="password"
                      name="password"
                    />
                  </div>

                  <button type="submit" className="btn btn-primary w-100">
                    Login
                  </button>
                </form>
              </div>
            </div>

            <div className="text-center mt-4">
              Don't have account? <Link to="/register">Register</Link>
            </div>
          </div>
        </div>
      </div>
    </main>
  );
}

function Navbar() {
  return (
    <nav className="navbar navbar-expand-lg bg-body-tertiary fixed-top">
      <div className="container">
        <Link to={"/"} className="navbar-brand m-auto">
          Facegram
        </Link>
      </div>
    </nav>
  );
}

class App extends Component {
  doLogin = (username, password) => {
    const apiUrl = "http://localhost:8000/api/v1/auth/login";

    axios
      .post(apiUrl, { username, password })
      .then((response) => {
        if (response.data && response.data.status === 401) {
          document.getElementById('alert').classList.toggle('d-none')
          document.getElementById('alert').innerHTML = 'Login Failed'
        } else if(response.data && response.data.status === 422) {
           console.log(response.data.errors)
        }
        console.log(response.data.message);
      })
      .catch((error) => {
      });
  };

  render() {
    return (
      <BrowserRouter>
        <div>
          <Navbar />
          <Routes>
            <Route
              path="/login"
              element={<LoginPage doLogin={this.doLogin} />}
            />
            {/* Add other routes as needed */}
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
