import React, { Component, useState } from "react";
import { BrowserRouter, Route, Link, Routes } from "react-router-dom";
import axios from "axios";
import Nav from '../components/nav';



const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  let element = ['username', 'password']
  let warn = document.getElementById('alert')

  const handleSubmit = (e) => {
    e.preventDefault();
    axios({
      method: "post",
      url: "http://localhost:8000/api/v1/auth/login",
      data: {
        username: username,
        password: password,
      }
    }).then((response) => {
      // console.log(response)
    }).catch((error) => {
      console.log(error.response)
      if(error.response.status == 401){
        warn.classList.remove('d-none')
        warn.innerHTML = error.response.data.message
      }else if(error.response.status == 422){
        for(let i = 0; i <= 3; i++){
          document.getElementById(element[i] + 'errors').innerHTML = '';
      }
      for (const key in error) {
        if (error.hasOwnProperty(key.data.message)) {
          document.getElementById(key.data.mess + 'errors').innerHTML = error[key];
        }
      }
      }
    })
  }
    return (
        <React.Fragment>
          <Nav />
        <main className="mt-5">
          <div className="container py-5">
            <div className="row justify-content-center">
              <div className="col-md-5">
                <div className="card">
                  <div className="card-header d-flex align-items-center justify-content-between bg-transparent py-3">
                    <h5 className="mb-0">Login</h5>
                  </div>
                  <div className="card-body">
                  <p className="alert alert-danger d-none" id="alert"></p>
                    <form onSubmit={handleSubmit}>
                      <div className="mb-2">

                        <label htmlFor="username">Username</label>
                        <p className="small m-0 p-0 text-danger" id="usernameerrors"></p>
                        <input
                          type="text"
                          className="form-control"
                          value={username}
                          onChange={(e) => setUsername(e.target.value)}
                          id="username"
                          name="username"
                        />
                      </div>

                      <div className="mb-3">
                        <label htmlFor="password">Password</label>
                        <input
                          type="password"
                          className="form-control"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          id="password"
                          name="password"
                        />
                      </div>

                      <button type="submit" className="btn btn-primary w-100">
                        Login
                      </button>
                    </form>
                  </div>
                </div>

                <div className="text-center mt-4">
                  Don't have account? <Link to={'../register'}>Register</Link>
                </div>
              </div>
            </div>
          </div>
        </main>
      </React.Fragment>
    );
}

export default Login;
